mapper.select:
  		SELECT checkout_result FROM t_checkout_detail WHERE is_valid=1
  		and checkout_datetime is not null

			and member_id= #{memberId}


			AND checkout_name= #{checkoutName}


			AND checkout_code= #{checkoutCode}


			AND checkout_datetime <= #{checkoutDatetime}

 		ORDER BY checkout_datetime DESC LIMIT 1

SELECT`sid`,`checkout_title`,`checkout_id`,`checkout_doctor_id`,`checkout_doctor_name`,`sample_code`,`sample_name`,`checkout_date`,
`checkout_time`,`checkout_remark`,`checkout_goal`,`yz_id`,`diagnose_result`,`diagnose_text`,`depart_code`,`member_id`,`visit_no`,department_name,
record_origin,is_valid,hospital_id,hospital_name
FROM t_checkout
where is_valid=1

	AND member_id = #{memberId}


	AND checkout_date >= #{startDt}


	AND checkout_date <= #{endDt}


	AND hospital_id = #{hospitalId}


	AND record_origin = #{recordOrigin}

order by report_date desc

select d.sid, d.checkout_id, d.checkout_code, d.checkout_name, d.checkout_result, d.result_unit, d.qualitative_result, d.reference_range, d.abnormal_sign, d.acronym, d.checkout_result_last, d.checkout_datetime,d.is_valid,d.hospital_id,d.hospital_name from t_checkout_detail d where d.checkout_id in
#{checkoutId}
and d.member_id=#{memberId}
and (d.hospital_id = #{hospitalId} or d.hospital_id=-1)
and d.is_valid=1 order by d.checkout_datetime desc;

select t.`checkout_id`,t.checkout_title, ifnull(t.sample_name,'---') sample_name, t.record_origin, t.hospital_name,t.checkout_time, t.department_name,t.checkout_doctor_name from t_keynote_configure a JOIN t_doctor_keynote b on b.keynote_id=a.keynote_id and a.is_valid=1 and b.is_valid=1
and b.doctor_id=#{keyNoteModel.doctorId} and b.in_hos=#{keyNoteModel.inHos} and b.hospital_id=#{keyNoteModel.hospitalId} and b.type=#{keyNoteModel.type}
left JOIN (SELECT t.`sid`,t.`checkout_id`,t.`checkout_code`,t.`checkout_name`, t.hospital_id,t.hospital_name,t.is_valid,c.record_origin,c.sample_name,c.department_name, c.checkout_doctor_name,c.checkout_title,c.checkout_time FROM t_checkout_detail t join t_checkout c on c.hospital_id=t.hospital_id and c.checkout_id = t.checkout_id and c.member_id=t.member_id where t.is_valid=1 and c.is_valid=1
and (t.checkout_name in
#{item.displayName}
OR t.checkout_code in
#{item.checkoutCode} )
and t.member_id=#{memberId}
and (t.hospital_id = #{hospitalId} or t.hospital_id = -1)
and t.checkout_time >= #{startDt}
and t.checkout_time <= #{endDt}
order by c.checkout_time desc)t on t.checkout_name = a.display_name where t.sid is not null group by t.checkout_title,t.checkout_time order by t.checkout_time desc
