mapper.select:
select
sid, sort, group_code, recommend_course, title, answer_options, answer_key, analyse, type, two_type,ques_type, ques_sign,
ques_tag, lever, remark, is_valid, insert_dt, modify_dt
from tiq_daily_question

    is_valid = 1

        and sid = #{sid}


        and sort = #{sort}


        and group_code = #{groupCode}


        and recommend_course = #{recommendCourse}


        and title = #{title}


        and type = #{type}


        and two_type = #{twoType}


        and ques_sign = #{quesSign}


        and ques_tag = #{quesTag}


        and lever = #{lever}



order by insert_dt desc

select
sid, sort, group_code, recommend_course, title, answer_options, answer_key, analyse, type, two_type,ques_type, ques_sign,
ques_tag, lever, remark, is_valid, insert_dt, modify_dt
from tiq_daily_question

    is_valid = 1

        and sid = #{sid}


        and sort = #{sort}


        and group_code = #{groupCode}


        and recommend_course = #{recommendCourse}


        and title = #{title}


        and type = #{type}


        and two_type = #{twoType}


        and ques_sign = #{quesSign}


        and ques_tag = #{quesTag}


        and lever = #{lever}



order by insert_dt asc,sid asc

select
sid, sort, group_code, recommend_course, title, answer_options, answer_key, analyse, type, two_type,ques_type, ques_sign,
ques_tag, lever, remark, is_valid, insert_dt, modify_dt
from tiq_daily_question

    is_valid = 1

        and sid = #{sid}


        and sort = #{sort}


        and group_code = #{groupCode}


        and title = #{title}


        and type = #{type}


        and two_type = #{twoType}


        and ques_sign = #{quesSign}


        and ques_tag = #{quesTag}


order by sort asc
limit 1

select sid, sort, group_code, recommend_course, title, answer_options, answer_key, analyse, type, two_type,ques_type, ques_sign, ques_tag, lever, remark, is_valid, insert_dt, modify_dt from tiq_daily_question where is_valid = 1 and two_type = 2 and type in
#{type}
and sid not in (select qid sid from tiq_daily_question_push where member_id = #{memberId}) order by type asc,sort asc

mapper.update:
update tiq_daily_question
sort = #{sort}, group_code = #{groupCode}, recommend_course = #{recommendCourse}, title = #{title}, answer_options = #{answerOptions}, answer_key = #{answerKey}, analyse = #{analyse}, type = #{type}, two_type = #{twoType}, ques_sign = #{quesSign}, ques_tag = #{quesTag}, lever = #{lever}, remark = #{remark}, modify_dt = now()
where sid = #{sid}
