mapper.select:
select
sid, machine_no,is_valid, machine_type
from t_bp_member_machine


        and machine_no = #{machineNo}


        and member_id = #{memberId}


order by insert_dt desc

select
sid, machine_no,member_id, modify_dt, insert_dt, is_valid,
 operation_type, operation_id,machine_type,plan_start_dt,plan_end_dt
from t_bp_member_machine


        and sid = #{sid}


        and machine_no = #{machineNo}


        and member_id = #{memberId}


        and modify_dt = #{modifyDt}


        and insert_dt = #{insertDt}


        and is_valid = #{isValid}


        and operation_type = #{operationType}


        and operation_id = #{operationId}


        and machineType = #{machine_type}

select m.sid, m.machine_no,m.is_valid, m.machine_type,t.member_name,t.member_id, t.birthday,t.sex,t.id_card,c.checkin_status,m.plan_start_dt,m.plan_end_dt from t_bp_member_machine m left join t_member t on m.member_id = t.member_id left join t_member_checkin_info c on c.member_id = t.member_id
m.is_valid = 1
and machine_no = #{machineNo}
and member_id = #{memberId}
and (instr(t.member_name, #{keyword}) > 0 or instr(t.id_card, #{keyword}) > 0 )
order by m.insert_dt desc

mapper.update:
update t_bp_member_machine set is_valid = 0,modify_dt = now()
and sid in
#{item}
