mapper.select:
select
sid, member_id, operation_id, arm_line, arms_on, cuff, work_shift, noon_sleep_start, noon_sleep_end,
night_sleep_start, bed_dt, breakfast_dt, lunch_dt, dinner_dt,
nervous, symptom, remark,start_dt,end_dt,insert_dt, modify_dt, is_valid,machine_no
from  t_dynamic_bp_diary


        and sid = #{sid}


        and member_id = #{memberId}


        and operation_id = #{operationId}


        and arm_line = #{armLine}


        and arms_on = #{armsOn}


        and cuff = #{cuff}


        and work_shift = #{workShift}


        and noon_sleep_start = #{noonSleepStart}


        and noon_sleep_end = #{noonSleepEnd}


        and night_sleep_start = #{nightSleepStart}


        and bed_dt = #{bedDt}


        and breakfast_dt = #{breakfastDt}


        and lunch_dt = #{lunchDt}


        and dinner_dt = #{dinnerDt}


        and nervous = #{nervous}


        and symptom = #{symptom}


        and remark = #{remark}


        and start_dt = #{startDt}


        and end_dt = #{endDt}


        and insert_dt = #{insertDt}


        and modify_dt = #{modifyDt}


        and is_valid = #{isValid}

mapper.update:
update t_dynamic_bp_diary
operation_id = #{operationId,jdbcType=INTEGER}, arm_line = #{armLine,jdbcType=VARCHAR}, arms_on = #{armsOn,jdbcType=VARCHAR}, cuff = #{cuff,jdbcType=VARCHAR}, work_shift = #{workShift,jdbcType=VARCHAR}, noon_sleep_start = #{noonSleepStart,jdbcType=VARCHAR}, noon_sleep_end = #{noonSleepEnd,jdbcType=VARCHAR}, night_sleep_start = #{nightSleepStart,jdbcType=VARCHAR}, bed_dt = #{bedDt,jdbcType=VARCHAR}, breakfast_dt = #{breakfastDt,jdbcType=VARCHAR}, lunch_dt = #{lunchDt,jdbcType=VARCHAR}, dinner_dt = #{dinnerDt,jdbcType=VARCHAR}, nervous = #{nervous,jdbcType=VARCHAR}, symptom = #{symptom,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, machine_no = #{machineNo,jdbcType=INTEGER}, modify_dt = now()
sid = #{sid}
and start_dt = #{startDt}
and end_dt = #{endDt}
and member_id = #{memberId}
