mapper.select:
select DATE_FORMAT(record_time,'%H:%i') from dy_yp_blood_sugar
where is_valid=1

  and record_dt>=#{startDt}


  and record_dt<=#{endDt}


  and sensor_no=#{sensorNo}

GROUP BY DATE_FORMAT(record_time,'%H:%i')
order by DATE_FORMAT(record_time,'%H:%i');

select CONCAT(#{endDt},' ',#{time},':00') record_time,sid, batch_id, value, record_dt,
param_code, record_origin, level, remark, is_valid, insert_dt, modify_dt from dy_yp_blood_sugar
where is_valid=1
and DATE_FORMAT(record_time,'%H:%i')=#{time}

  and record_dt>=#{startDt}


  and record_dt<=#{endDt}


  and sensor_no=#{sensorNo}

order by `value` ASC;

select count(1) from (select record_dt,count(1) num from dy_yp_blood_sugar
where record_dt >= #{startDt} and record_dt <= #{endDt}

  and sensor_no=#{sensorNo}

GROUP BY record_dt) t
where t.num>=96

select  sid, batch_id, value, record_dt, record_time, param_code, record_origin, level, remark,
is_valid, insert_dt, modify_dt, sensor_no, machine_no from dy_yp_blood_sugar  t
where t.is_valid = 1 and sensor_no = #{sensorNo} and record_time between #{startTime} and #{endTime}

(SELECT record_dt,record_time,`value` from DY_YP_BLOOD_SUGAR where sensor_no=#{sensorNo}
and record_dt>=#{startDt} and record_dt <=#{endDt} GROUP BY record_dt ORDER BY record_time)
union all
(select record_dt,record_time,`value` from
(SELECT record_dt,record_time,`value` from DY_YP_BLOOD_SUGAR where sensor_no=#{sensorNo}
and record_dt>=#{startDt} and record_dt <=#{endDt} ORDER BY record_time desc) a GROUP BY a.record_dt)

select *
from DY_YP_BLOOD_SUGAR
where 1=1 

  and sensor_no = #{sensorNo}


  and is_valid = #{valid}


  and record_time >= #{startTime}


  and #{endTime} >= record_time


  and record_dt in

    #{item}


order by record_time asc
