mapper.select:
select count(*) from (
select t3.member_id from t_research_group t1,t_research_group_member t2,dy_member_sensor t3
where t1.valid = 1 and t2.valid = 1 and t3.is_valid = 1
and t2.research_group_id = t1.group_id
and t2.member_id = t3.member_id
and t1.hospital_id = #{hospitalId}

    and t1.group_id = #{researchGroupId}

and t3.insert_dt between DATE_FORMAT(t2.insert_dt,'%Y-%m-%d') and #{endDt}
group by t3.member_id
) r

select'true' as QUERYID,sid, sensor_no, type, start_dt, day, avg_num, ghb, standard_val, awi_time_rate_of_normal,
awi_time_rate_of_high, awi_time_rate_of_low, event_count_of_normal, event_count_of_high,
event_count_of_low, avg_awi_time_of_normal, avg_awi_time_of_high, avg_awi_time_of_low,
mean_amplitude_of_glycemic_excursion, awi_time_rate_of_4_0, awi_time_rate_of_13_9,
awi_time_rate_of_3_9, coefficient_of_variation, is_valid, insert_dt, modify_dt,chart_data, day_sugar_avg_diff_val_json
from dy_yp_statistics
where 1=1

    and sensor_no = #{sensorNo}


    and start_dt = #{startDt}


    and day = #{day}


    and type = #{type}


    and is_valid = #{valid}
