mapper.select:
SELECT

FROM t_knowledge t


		AND t.id = #{id}


		AND t.insert_dt = #{insertDt}


		AND t.modify_dt = #{modifyDt}


		AND t.categoryId = #{categoryId}


		AND t.is_valid = #{isValid}


		AND t.name like CONCAT('%',#{name},'%')


		AND t.pid = #{pid}


		AND t.has_child = #{hasChild}


		AND t.sort = #{sort}


		AND t.zindex = #{zindex}


		AND t.operate_id = #{operateId}



 order by t.py asc  


 order by t.sort asc

SELECT 

FROM t_feature t
WHERE t.id = #{id}

SELECT 

FROM t_feature_item t
WHERE t.id = #{id}

SELECT

FROM t_cfg_tag t


		AND t.id = #{id}


		AND t.lable = #{lable}


		AND t.code = #{code}


		AND t.insert_dt = #{insertDt}


		AND t.update_dt = #{updateDt}


		AND t.is_valid = #{isValid}


		AND t.operate_id = #{operateId}



	order by py asc


	order by py asc

SELECT

FROM t_cfg_keyword t


		AND t.id = #{id}


		AND t.lable like CONCAT('%',#{lable},'%')


		AND t.code = #{code}


		AND t.insert_dt = #{insertDt}


		AND t.update_dt = #{updateDt}


		AND t.is_valid = #{isValid}


		AND t.operate_id = #{operateId}



	order by py asc


	order by id asc

SELECT 

FROM t_category_rule t
WHERE t.id = #{id}

		SELECT 

		FROM t_category_rule t
        where t.category_id = #{categoryId}

