mapper.update:
update t_member_join_hospital
set update_dt = now()

    , attention = #{attention}


    , valid = #{valid}

where sid = #{sid}

mapper.select:
select sid, member_id, hospital_id, join_type, foreign_id, operator_type, operator_id,
in_date, ext_data, insert_dt, update_dt, valid
from t_member_join_hospital where valid = 1

    and member_id = #{memberId}


    and hospital_id = #{hospitalId}

limit 1

select sid, member_id, hospital_id, join_type, foreign_id, operator_type, operator_id,
in_date, ext_data, insert_dt, update_dt, valid
from t_member_join_hospital where valid = 1

    and member_id = #{memberId}


    and hospital_id = #{hospitalId}


    and hospital_id in

        #{hospitalId}



    and in_date >= #{begin}


    and in_date <= #{end}

select mjh.sid,#{doctorId} doctor_id, m.member_id, m.member_name, m.mobile_phone, m.sex, m.birthday, m.id_card, case when m.is_diabetes = 1 and m.essential_hyp = 1 then '高血压-糖尿病' when m.is_diabetes = 1 then '糖尿病' when m.essential_hyp = 1 then '高血压' else '无' end diabetesType, IF(mci.checkin_status = 1, 1, 2) status, hospital_name, if(mjh.hospital_id = mci.hospital_id, 1, 0) thisHospital, m.insert_dt filingDate, mjh.attention from t_member_join_hospital mjh join t_member m on m.member_id = mjh.member_id left join (select member_id,checkin_status,hospital_id from t_member_checkin_info where is_valid = 1) mci on mci.member_id = mjh.member_id left join t_hospital h on mci.hospital_id = h.hospital_id where 1=1 and mjh.valid = 1
and (instr(m.member_name, #{keyword}) or instr(m.member_name_py, #{keyword}) or instr(m.mobile_phone, #{mobilePhone}))
and IF(mci.checkin_status = 1, 1, 2) = #{status}
and m.insert_dt >= #{begin}
and #{end} >= m.insert_dt
and attention = #{attention}
and mjh.hospital_id = #{hospitalId}
and m.is_diabetes = 1
and m.essential_hyp = 1
order by mjh.attention desc, m.insert_dt desc

select m.member_id, m.member_name, m.mobile_phone, m.sex, m.birthday, m.is_diabetes, m.essential_hyp, m.pic_url, #{doctorId} doctorId, if(mci.member_id is not null , 1, 0) inHos,m.committee_id from t_member_join_hospital mjh join t_member m on m.member_id = mjh.member_id left join t_member_checkin_info mci on mci.member_id = mjh.member_id and mci.checkin_status = 1 and mci.is_valid = 1 where 1=1 and mjh.valid = 1
and (instr(m.member_name, #{keyword}) or instr(m.member_name_py, #{keyword}) or instr(m.mobile_phone, #{mobilePhone}))
and mjh.hospital_id = #{hospitalId}
AND mjh.member_id IN
#{item}
and m.committee_id in
#{item}
order by mjh.attention desc, m.insert_dt desc

select mjh.sid, m.member_id, m.member_name, m.mobile_phone, m.sex, m.birthday,mjh.valid,m.pic_url,m.committee_id ,m.committee_name from t_member_join_hospital mjh join t_member m on m.member_id = mjh.member_id where 1=1 and mjh.valid = 1
and mjh.hospital_id = #{hospitalId}
and DATE_FORMAT(mjh.insert_dt,'%Y-%m-%d %H:%i:%s') < #{refreshTime}
order by mjh.insert_dt desc

select m.member_id, m.member_name, m.mobile_phone, m.sex, m.birthday, DATE_FORMAT(fm.fill_dt,'%Y-%m-%d') lastFollowDt,fm.doctor_id,fm.doctor_name, case when fm.type = 7 then '糖尿病随访' when fm.type = 11 then '高血压随访' else '其他随访' end followType, case when m.is_diabetes = 1 and m.essential_hyp = 1 then '高血压-糖尿病' when m.is_diabetes = 1 then '糖尿病' when m.essential_hyp = 1 then '高血压' else '无' end diabetesType from t_member_join_hospital mjh join t_member m on m.member_id = mjh.member_id left join (select * from (select type,fill_dt,member_id,doctor_name,doctor_id from t_follow_main where type in (7,11) and is_valid = 1 and status = 1 having 1 /*keep orderby*/ order by fill_dt desc) a group by member_id) fm on fm.member_id = mjh.member_id where mjh.valid = 1
and mjh.hospital_id = #{hospitalId}
and m.committee_id in
#{item}
order by fm.fill_dt desc,mjh.insert_dt desc

select mjh.sid, m.member_id, m.member_name, m.mobile_phone, m.sex, m.birthday,mjh.valid,m.pic_url, m.committee_id from t_member_join_hospital mjh join t_member m on m.member_id = mjh.member_id where mjh.valid = 1
and mjh.hospital_id = #{hospitalId}
and DATE_FORMAT(m.modify_dt,'%Y-%m-%d %H:%i:%s') >= #{refreshTime}
order by m.modify_dt desc
