    AND t.record_time >= #{startDt}


    AND t.record_time <= #{endDt}

and (t.sbp_level=1 or t.dbp_level=1)

select sid, dbp, sbp, member_id, record_time, sbp_level, dbp_level, insert_dt, is_valid,
update_dt, operator_type, operator_id, origin,dbp_min ,dbp_max ,sbp_min ,sbp_max
from t_blood_pressure where is_valid = 1

    and sid = #{sid}


    and member_id = #{memberId}


    and record_time = #{recordTime}

limit 1

select.if:
select * from (SELECT sid, dbp, sbp, tbp.member_id, record_time, sbp_level, dbp_level, insert_dt, is_valid, update_dt, operator_type, operator_id, origin FROM t_blood_pressure tbp , ( select m.member_id from t_member m join t_member_checkin_info mci on mci.member_id = m.member_id and mci.is_valid =1 join t_member_in_hospital_log h on m.member_id=h.member_id where h.is_valid=1 and mci.checkin_status = 1
and h.hospital_id=#{hospitalId}
and (h.in_hospital_date < #{endDt} and (h.out_hospital_date > #{startDt} or h.out_hospital_date is null))
and m.is_diabetes=#{isDiabetes}
and m.sex=#{sex}
GROUP BY m.member_id )t where t.member_id=tbp.member_id
and tbp.insert_dt BETWEEN #{startDt} and #{endDt}
order by tbp.record_time desc)t GROUP by t.member_id

select * from (SELECT sid, dbp, sbp, tbp.member_id, record_time, sbp_level, dbp_level, insert_dt, is_valid, update_dt, operator_type, operator_id, origin FROM t_blood_pressure tbp , ( select m.member_id from t_member m join t_member_join_hospital mjh on m.member_id = mjh.member_id and mjh.valid = 1
and mjh.hospital_id=#{hospitalId}
left join (select member_id,checkin_status,hospital_id from t_member_checkin_info where is_valid = 1) mci on mci.member_id = mjh.member_id where m.is_valid=1 and(mci.checkin_status is null or mci.checkin_status != 1)
and m.sex=#{sex}
and m.is_diabetes=#{isDiabetes}
GROUP BY m.member_id )t where t.member_id=tbp.member_id
and tbp.insert_dt BETWEEN #{startDt} and #{endDt}
order by tbp.record_time desc)t GROUP by t.member_id

select * from (SELECT sid, dbp, sbp, tbp.member_id, record_time, sbp_level, dbp_level, tbp.insert_dt, is_valid, update_dt, operator_type, operator_id, origin FROM t_blood_pressure tbp , ( select q.member_id,e.insert_dt join_dt from t_member q ,t_research_group w,t_research_group_member e where q.is_valid = 1 and w.valid = 1 and e.valid = 1 and q.member_id = e.member_id and w.group_id = e.research_group_id and w.hospital_id = #{hospitalId}
and w.group_id = #{researchGroupId}
group by q.member_id )t where t.member_id=tbp.member_id and tbp.insert_dt BETWEEN DATE_FORMAT(t.join_dt,'%Y-%m-%d') and #{endDt} order by tbp.record_time desc)t GROUP by t.member_id

mapper.select:
SELECT sid, dbp, sbp, tbp.member_id, record_time, sbp_level, dbp_level, insert_dt, is_valid,
update_dt, operator_type, operator_id, origin
FROM t_blood_pressure tbp , (
select m.member_id from t_member m
join t_member_checkin_info mci on mci.member_id = m.member_id and mci.is_valid =1
join t_member_in_hospital_log h on m.member_id=h.member_id
where h.is_valid=1 and mci.checkin_status = 1

    and h.hospital_id=#{hospitalId}


    and h.out_hospital_date between #{startDt} and #{endDt}
    and in_status=0


    and m.is_diabetes=#{isDiabetes}


    and m.sex=#{sex}

GROUP BY m.member_id
)t
where t.member_id=tbp.member_id

    and tbp.insert_dt BETWEEN #{startDt} and #{endDt}

select
distinct t.member_id
from (select a.* from (select sid, dbp, sbp, member_id, record_time, sbp_level, dbp_level, insert_dt, is_valid,
update_dt, operator_type, operator_id, origin,dbp_min ,dbp_max ,sbp_min ,sbp_max
from t_blood_pressure
having 1
order by record_time desc) a
group by a.member_id) t
join t_member_join_hospital h on t.member_id = h.member_id

    t.is_valid = 1 and h.valid = 1

        and t.member_id = #{memberId}


        and t.record_time >= #{startDt}


        and t.record_time <= #{endDt}


        and h.hospital_id = #{hospitalId}


        AND h.hospital_id IN

            #{item}




            and t.sbp_level = '3' and t.dbp_level = '3'


            and (t.sbp_level != '3' or t.dbp_level != '3')

select t.sid, t.dbp, t.sbp, t.member_id, t.record_time, t.sbp_level, t.dbp_level, t.insert_dt, t.is_valid,
t.update_dt, t.operator_type, t.operator_id, t.origin ,t.dbp_min ,t.dbp_max ,t.sbp_min ,t.sbp_max,t.heart_rate
from t_blood_pressure t left join t_member_join_hospital h on t.member_id = h.member_id

    t.is_valid = 1 and h.valid = 1

        and t.member_id = #{memberId}


        and t.record_time >= #{startDt}


        and t.record_time <= #{endDt}


        and h.hospital_id = #{hospitalId}


        AND h.hospital_id IN

            #{item}




            and t.sbp_level = '3' and t.dbp_level = '3'


            and (t.sbp_level != '3' or t.dbp_level != '3')



order by t.record_time desc
